import React, { useState } from "react";

import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle,setEnteredTitle]=useState('');
    const [enteredAmount,setEnteredAmount]=useState('');
    const [enteredDate,setEnteredDate]=useState('');
    const titleChangeHandler = (event) => {                //since onChange is pointing to this function, this funvtion has access tot he event object
        // console.log(event.target.value)                    //evengt.target.value will give current updated value with every change i.e.,keystroke
        setEnteredTitle(event.target.value)
    }
    const amountChangeHandler = (event) => {                //since onChange is pointing to this function, this funvtion has access tot he event object
        // console.log(event.target.value)                    //evengt.target.value will give current updated value with every change i.e.,keystroke
        setEnteredTitle(event.target.value)
    }
    const dateChangeHandler = (event) => {                //since onChange is pointing to this function, this funvtion has access tot he event object
        // console.log(event.target.value)                    //evengt.target.value will give current updated value with every change i.e.,keystroke
        setEnteredTitle(event.target.value)
    }
    return <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type='text' onChange={titleChangeHandler}/>     {/*advantage of onChange it works for every keystroke and it works for drop downs */}
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type='number' onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type='date' onChange={dateChangeHandler}/>
            </div>
        </div>
        <div className="new-expense__actions">
            <button type='submit'>Add Expense</button>
        </div>
    </form>
};

export default ExpenseForm;
