import React from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
import './ExpensesFilter.css';

function Expenses(props) {  //component functions can be arrow functions as well
    const [dateFilter, setDateFilter] = React.useState('2020');

    const filterChangeHandler = (selectedYear) => {
        // console.log("selected date",selectedYear);
        setDateFilter(selectedYear);
        // console.log(dateFilter);
    }
    const filteredArray=props.items.filter(expense=> expense.date===new Date(datefilter))
    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selectedDate={dateFilter} onAddDate={filterChangeHandler} />
                {filteredArray.map(expense => (
                    <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date} />
                ))}
            </Card>
        </div>
    );
};

export default Expenses
